con:config.script:
soapStepName = 'Subtraction'
responseNode = 'MultiplyResult'

// Read from Properties test step (NOT TestCase)
expectedValue = context.expand('${Properties#output}')

log.info "ExpectedValue = '${expectedValue}'"

// Fail fast if empty
assert expectedValue?.trim() :
        "FAIL: Property 'output' in Properties step is empty or missing"

// Call external script
evaluate(new File('C:/Users/PC/Downloads/DeleteLater_SoapUITesting/validation.groovy'))

con:soapui-project.con:beforeRunScript:
import com.eviware.soapui.support.XmlHolder

// Ensure project context is available
if (!context || !context.getProject()) {
    log.error "âŒ ERROR: Project context is null. Make sure to run this in 'Project Setup Script'."
    return
}

// Get the project reference explicitly
def project = context.getProject()

// Define correct endpoint mappings based on both Test Case & Test Step
def endpointMappings = [
    "TestCase 1" : [ 
        "Addition"       : "http://www.dneonline.com/calculator.asmx",
        "Subtraction"    : "http://www.dneonline.com/calculator.asmx"
    ],
    "TestCase 2" : [ 
        "Addition"       : "http://api.example.com/math/add",
        "Subtraction"    : "http://api.example.com/math/subtract"
    ]
]

// List to store fixed endpoints for logging
def fixedEndpoints = []

// Iterate through all test suites, test cases, and test steps
project.getTestSuiteList().each { suiteObj ->
    suiteObj.getTestCaseList().each { caseObj ->
        def caseName = caseObj.getName()

        // Check if this test case has defined mappings
        if (endpointMappings.containsKey(caseName)) {
            def testStepMappings = endpointMappings[caseName]  // Get the mapping for this test case

            caseObj.getTestStepList().each { stepObj ->
                def stepName = stepObj.getName()

                // Check if this test step has a correct mapping inside this test case
                if (testStepMappings.containsKey(stepName)) {
                    def correctEndpoint = testStepMappings[stepName]  // Get the correct endpoint

                    // Check if it's a SOAP or REST request (without sending any requests)
                    if (stepObj.metaClass.respondsTo(stepObj, "getHttpRequest")) {
                        def request = stepObj.getHttpRequest()
                        def currentEndpoint = request.getEndpoint() // Get current endpoint

                        // Fix the endpoint only if incorrect
                        if (currentEndpoint != correctEndpoint) {
                            request.setEndpoint(correctEndpoint)
                            fixedEndpoints << "âœ… Fixed in ${suiteObj.getName()} â†’ ${caseName} â†’ ${stepName}: ${currentEndpoint} â†’ ${correctEndpoint}"
                        }
                    }
                }
            }
        }
    }
}

// Print fixed endpoints
if (fixedEndpoints) {
    log.info "======= ðŸ›  Self-Healing Endpoint Fixes ======="
    fixedEndpoints.each { log.info it }
} else {
    log.info "âœ… No endpoint issues detected."
}
